INSERT INTO authors VALUES (1, 'Иван', 'Крылов', 'Иван Андреевич Крылов — русский писатель, наиболее значимый баснописец в истории русской словесности; журналист, издатель сатирико-просветительских журналов, библиограф.');
INSERT INTO authors VALUES (2, 'Михаил', 'Лермонтов', 'Михаил Лермонтов — один из самых известных русских поэтов, и признание к нему пришло еще при жизни. Его творчество, в котором сочетались острые социальные темы с философскими мотивами и личными переживаниями, оказало огромное влияние на поэтов и писателей XIX–XX веков. «Культура.РФ» рассказывает о личности, жизни и творчестве Михаила Лермонтова.');
INSERT INTO books VALUES (1, 'Забавные истории с запоминающейся моралью — это конечно же лучшие из лучших басен Крылова.', 'Басни', 1);